import R from 'ramda'
import {
    ADD_PHONE_TO_BASKET
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch(type) {
        case ADD_PHONE_TO_BASKET:
            // append добавляет id в initialState. 
            // В payload приходит новый id. Этот id добавляется в конец массива
            return R.append(payload, state)
        default:
            return state
    }
}