import R from 'ramda'
import {
    ADD_PHONE_TO_BASKET,
    REMOVE_PHONE_FROM_BASKET,
    CLEAN_BASKET
} from 'actionTypes'

const initialState = []

export default (state = initialState, {type, payload}) => {
    switch(type) {
        case ADD_PHONE_TO_BASKET:
            // append добавляет id в initialState. 
            // В payload приходит новый id. Этот id добавляется в конец массива
            return R.append(payload, state)
        case REMOVE_PHONE_FROM_BASKET:
            // without удалит все id, которые встретятся из этого id в массиве
            // принимает первыйм параметром массив элементов, которые хотим удалить
            return R.without([payload], state)
        case CLEAN_BASKET: 
            return []
        default:
            return state
    }
}