import R from 'ramda'

import {
    FETCH_PHONE_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {
    // id необходимого телефона, который получили, когда зашли на страницу телефона
    id: null
}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONE_BY_ID_SUCCESS:
            return R.merge(state, {
                // получаем id из payload. То же что и payload.id
                id: R.prop('id', payload)
            })
        default:
            return state
    }
}