import R from 'ramda'

import {
    FETCH_PHONES_SUCCESS,
    LOAD_MORE_PHONES_SUCCESS,
    FETCH_PHONE_BY_ID_SUCCESS,
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload} ) => {
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            // получаем объект с ключами-id, каждому из которых записан соответствующий телефон
            const newValues = R.indexBy(R.prop('id'), payload) // payload - наши данные
            // перетрем все значения в state 
            return R.merge(state, newValues)

        case LOAD_MORE_PHONES_SUCCESS: // чтобы редьюсер подписывался на этот action
            const moreValues = R.indexBy(R.prop('id'), payload)
            return R.merge(state, moreValues)

        // когда срабатывает этот метод, хотим добавить полученные данные в наш объект
        case FETCH_PHONE_BY_ID_SUCCESS:
            // хотим добавить в объект state как ключ payload.id, а в этот ключ записать содержимое value -  payload
            return R.assoc(payload.id, payload, state)
        default:
            return state
    }
}