import R from 'ramda'

import {
    FETCH_PHONES_SUCCESS,
    LOAD_MORE_PHONES_SUCCESS,
    SEARCH_PHONE
} from 'actionTypes'

const initialState = {
    ids: [],
    search: ''
}

export default (state = initialState, {type, payload}) => {
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            // доб в state новый объект
            // state измениться, все id соберуться в initialState в ids: []
            return R.merge(state, {
                // из полученных телефонов вытаскиваем id
                ids: R.pluck('id', payload)
            })

        case LOAD_MORE_PHONES_SUCCESS:
            // найдем все id которые мы получили в результате этого метода
            const ids = R.pluck('id', payload)
            // хотим эти id добавить к уже имеющимся, не перетирая
            return R.merge(state, {
                ids: R.concat(ids, state.ids)
            })

        case SEARCH_PHONE:
            // меняем поле search в initialState на новое
            return R.merge(state, {
                search: payload
            })
        default:
            return state
    }
}